'''
SUPPORT FUNCTIONS
'''
def digitalizer(text:str)->list:
    return [abc.index(letter) for letter in text.upper()]


def characterizer(digital:list)->list:
    return [abc[number%abc_len] for number in digital]

def abc_freq():
    freqs = {'A':8.12, 'B':1.49, 'C':2.71, 'D':4.32, 'E':12.02, 'F':2.3, 'G':2.03, 'H':5.92,
             'I':7.31, 'J':0.1,'K':0.69, 'L':3.92,'M':2.61,'N':6.95,'O':7.68,'P':1.82,'Q':0.11,
            'R':6.02,'S':6.28,'T':9.10,'U':2.88,'V':1.11,'W':2.09,'X':0.17,'Y':2.11,'Z':0.07}
    return dict(sorted(freqs.items(), key= lambda x:x[1], reverse=True))
